CREATE OR REPLACE FUNCTION public.check_rate_limit(p_endpoint text, p_limit integer DEFAULT 100, p_window_minutes integer DEFAULT 60)
 RETURNS boolean
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
DECLARE
  v_user_id uuid;
  v_window_start timestamp with time zone;
  v_current_count integer;
BEGIN
  v_user_id := auth.uid();
  
  -- Return true if user is not authenticated (let other checks handle this)
  IF v_user_id IS NULL THEN
    RETURN true;
  END IF;
  
  -- Calculate window start time
  v_window_start := date_trunc('hour', now()) + 
    INTERVAL '1 hour' * FLOOR(EXTRACT(epoch FROM now() - date_trunc('hour', now())) / (p_window_minutes * 60));
  
  -- Get or create rate limit record
  INSERT INTO public.rate_limits (user_id, endpoint, window_start, request_count)
  VALUES (v_user_id, p_endpoint, v_window_start, 1)
  ON CONFLICT (user_id, endpoint, window_start)
  DO UPDATE SET 
    request_count = public.rate_limits.request_count + 1,
    created_at = now()
  RETURNING request_count INTO v_current_count;
  
  -- Check if limit exceeded
  IF v_current_count > p_limit THEN
    -- Log rate limit violation
    PERFORM public.log_security_event(
      'rate_limit_exceeded',
      v_user_id,
      'rate_limit',
      NULL,
      jsonb_build_object(
        'endpoint', p_endpoint,
        'count', v_current_count,
        'limit', p_limit,
        'window_minutes', p_window_minutes
      )
    );
    RETURN false;
  END IF;
  
  RETURN true;
END;
$function$
;

