CREATE OR REPLACE FUNCTION public.create_new_organization(org_name text, org_description text DEFAULT NULL::text)
 RETURNS uuid
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
DECLARE
  new_org_id uuid;
  user_id uuid;
  org_limit_check RECORD;
BEGIN
  -- Get the current user ID
  user_id := auth.uid();
  
  -- Validate that user is authenticated
  IF user_id IS NULL THEN
    RAISE EXCEPTION 'User must be authenticated to create organization';
  END IF;
  
  -- Check organization limit
  SELECT * INTO org_limit_check FROM public.check_user_org_limit(user_id) LIMIT 1;
  
  IF NOT org_limit_check.can_create THEN
    RAISE EXCEPTION 'Organization limit reached. You have % organizations out of % allowed on the % plan. Please upgrade to create more organizations.', 
      org_limit_check.current_count, org_limit_check.max_allowed, org_limit_check.plan_name;
  END IF;
  
  -- Validate organization name
  IF org_name IS NULL OR trim(org_name) = '' THEN
    RAISE EXCEPTION 'Organization name cannot be empty';
  END IF;
  
  -- Create the organization
  INSERT INTO public.organizations (name, description, slug)
  VALUES (
    trim(org_name),
    CASE WHEN org_description IS NOT NULL AND trim(org_description) != '' THEN trim(org_description) ELSE NULL END,
    lower(regexp_replace(trim(org_name), '[^a-zA-Z0-9]+', '-', 'g')) || '-' || substr(md5(random()::text), 0, 8)
  )
  RETURNING id INTO new_org_id;
  
  -- Add the user as owner of the new organization
  INSERT INTO public.organization_members (user_id, organization_id, role)
  VALUES (user_id, new_org_id, 'owner');
  
  -- Return the new organization ID
  RETURN new_org_id;
END;
$function$
;

