CREATE OR REPLACE FUNCTION public.execute_upgrade_intent(p_user_id uuid, p_stripe_session_id text)
 RETURNS TABLE(success boolean, action text, result_id uuid, message text)
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
DECLARE
  v_intent RECORD;
  v_new_org_id UUID;
  v_result_message TEXT;
BEGIN
  SELECT * INTO v_intent
  FROM upgrade_intents
  WHERE user_id = p_user_id 
    AND stripe_session_id = p_stripe_session_id
    AND NOT completed
    AND expires_at > now()
  ORDER BY created_at DESC
  LIMIT 1;

  IF NOT FOUND THEN
    RETURN QUERY SELECT false, ''::TEXT, NULL::UUID, 'No pending upgrade intent found'::TEXT;
    RETURN;
  END IF;

  IF v_intent.action = 'create_organization' THEN
    BEGIN
      SELECT create_new_organization(v_intent.organization_name, v_intent.organization_description) INTO v_new_org_id;
      v_result_message := 'Organization "' || v_intent.organization_name || '" created successfully';
    EXCEPTION WHEN OTHERS THEN
      v_result_message := 'Failed to create organization: ' || SQLERRM;
      v_new_org_id := NULL;
    END;
  ELSIF v_intent.action = 'invite_member' THEN
    v_new_org_id := v_intent.organization_id;
    v_result_message := 'Ready to invite ' || v_intent.invite_email || ' to organization';
  END IF;

  UPDATE upgrade_intents
  SET completed = true, completed_at = now()
  WHERE id = v_intent.id;

  RETURN QUERY SELECT 
    (v_new_org_id IS NOT NULL OR v_intent.action = 'invite_member'), 
    v_intent.action, 
    v_new_org_id, 
    v_result_message;
END;
$function$
;

