CREATE OR REPLACE FUNCTION public.get_or_create_user(p_clerk_user_id text, p_email text DEFAULT NULL::text, p_first_name text DEFAULT NULL::text, p_last_name text DEFAULT NULL::text)
 RETURNS TABLE(id uuid, clerk_user_id text, email text, first_name text, last_name text, created_at timestamp with time zone, updated_at timestamp with time zone, internal_id uuid)
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
DECLARE
  v_user_record RECORD;
BEGIN
  -- Try to find existing user with fully qualified column reference
  SELECT * INTO v_user_record
  FROM public.users u
  WHERE u.clerk_user_id = p_clerk_user_id;
  
  -- If user exists, return it (potentially updating name/email)
  IF FOUND THEN
    -- Update email and names if they've changed
    UPDATE public.users u
    SET 
      email = COALESCE(p_email, u.email),
      first_name = COALESCE(p_first_name, u.first_name),
      last_name = COALESCE(p_last_name, u.last_name),
      updated_at = now()
    WHERE u.clerk_user_id = p_clerk_user_id
    RETURNING * INTO v_user_record;
    
    RETURN QUERY SELECT 
      v_user_record.id,
      v_user_record.clerk_user_id,
      v_user_record.email,
      v_user_record.first_name,
      v_user_record.last_name,
      v_user_record.created_at,
      v_user_record.updated_at,
      v_user_record.internal_id;
    RETURN;
  END IF;
  
  -- User doesn't exist, create new one
  INSERT INTO public.users (clerk_user_id, email, first_name, last_name)
  VALUES (p_clerk_user_id, p_email, p_first_name, p_last_name)
  RETURNING * INTO v_user_record;
  
  RETURN QUERY SELECT 
    v_user_record.id,
    v_user_record.clerk_user_id,
    v_user_record.email,
    v_user_record.first_name,
    v_user_record.last_name,
    v_user_record.created_at,
    v_user_record.updated_at,
    v_user_record.internal_id;
END;
$function$
;

CREATE OR REPLACE FUNCTION public.get_user_current_plan(p_user_id uuid)
 RETURNS TABLE(plan_name text, max_organizations integer, max_members integer, organizations_used bigint, status subscription_status)
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
BEGIN
  -- First check if user has an active subscription with manual override
  RETURN QUERY
  SELECT 
    CASE 
      WHEN us.manual_override THEN 'Pro'
      ELSE sp.name 
    END as plan_name,
    CASE 
      WHEN us.manual_override THEN 5  -- Pro plan org limit
      ELSE sp.max_organizations_per_user 
    END as max_organizations,
    CASE 
      WHEN us.manual_override THEN 999999  -- Pro plan member limit
      ELSE sp.max_members_per_org 
    END as max_members,
    COALESCE(
      (SELECT COUNT(*) 
       FROM organization_members om 
       JOIN organizations o ON o.id = om.organization_id 
       WHERE om.user_id = p_user_id AND om.role = 'owner'), 
      0
    ) as organizations_used,
    us.status as status
  FROM user_subscriptions us
  JOIN subscription_plans sp ON sp.id = us.plan_id
  WHERE us.user_id = p_user_id
  
  UNION ALL
  
  -- Default to Free plan if no subscription exists
  SELECT 
    sp.name as plan_name,
    sp.max_organizations_per_user as max_organizations,
    sp.max_members_per_org as max_members,
    COALESCE(
      (SELECT COUNT(*) 
       FROM organization_members om 
       JOIN organizations o ON o.id = om.organization_id 
       WHERE om.user_id = p_user_id AND om.role = 'owner'), 
      0
    ) as organizations_used,
    'active'::subscription_status as status
  FROM subscription_plans sp
  WHERE sp.name = 'Free' 
    AND NOT EXISTS (SELECT 1 FROM user_subscriptions WHERE user_id = p_user_id)
  
  LIMIT 1;
END;
$function$
;

