CREATE OR REPLACE FUNCTION public.mask_sensitive_data(p_data jsonb, p_user_role organization_role DEFAULT 'member'::organization_role)
 RETURNS jsonb
 LANGUAGE plpgsql
 STABLE SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
DECLARE
  masked_data jsonb;
  sensitive_fields text[] := ARRAY[
    'stripe_customer_id',
    'subscription_id', 
    'stripe_subscription_id',
    'stripe_price_id',
    'stripe_product_id',
    'clerk_subscription_id',
    'token',
    'csrf_token'
  ];
  field text;
BEGIN
  masked_data := p_data;
  
  -- Only mask for non-admin roles
  IF p_user_role = 'member' THEN
    FOREACH field IN ARRAY sensitive_fields
    LOOP
      IF masked_data ? field THEN
        masked_data := masked_data - field;
      END IF;
    END LOOP;
  END IF;
  
  RETURN masked_data;
END;
$function$
;

