CREATE OR REPLACE FUNCTION public.set_manual_pro_subscription(p_user_id uuid)
 RETURNS void
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
DECLARE
  free_plan_id UUID;
BEGIN
  -- Get Free plan ID
  SELECT id INTO free_plan_id 
  FROM subscription_plans 
  WHERE name = 'Free' 
  LIMIT 1;
  
  -- Upsert user subscription with manual override
  INSERT INTO user_subscriptions (user_id, plan_id, status, manual_override)
  VALUES (p_user_id, free_plan_id, 'active', true)
  ON CONFLICT (user_id) 
  DO UPDATE SET 
    manual_override = true,
    updated_at = now();
END;
$function$
;

CREATE OR REPLACE FUNCTION public.set_user_context_by_clerk_id(p_clerk_user_id text, p_email text DEFAULT NULL::text, p_first_name text DEFAULT NULL::text, p_last_name text DEFAULT NULL::text)
 RETURNS uuid
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
DECLARE
  v_user_id uuid;
BEGIN
  -- Ensure user exists and get internal ID
  SELECT public.ensure_user_exists(p_clerk_user_id, p_email, p_first_name, p_last_name) INTO v_user_id;
  
  -- Set the user context for RLS
  PERFORM public.set_current_user_id(v_user_id);
  
  -- Log context setting
  RAISE NOTICE 'Set user context for clerk_user_id: % with internal id: %', p_clerk_user_id, v_user_id;
  
  RETURN v_user_id;
END;
$function$
;

CREATE OR REPLACE FUNCTION public.sync_organization_payment_status()
 RETURNS trigger
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
BEGIN
  -- Update all organizations owned by this user to reflect their subscription status
  UPDATE organizations 
  SET 
    is_paid = (
      SELECT CASE 
        WHEN sp.name != 'Free' AND us.status = 'active' THEN true 
        ELSE false 
      END
      FROM user_subscriptions us
      JOIN subscription_plans sp ON sp.id = us.plan_id
      WHERE us.user_id = NEW.user_id
      LIMIT 1
    ),
    subscription_status = CASE 
      WHEN NEW.status = 'active' THEN 'active'
      WHEN NEW.status = 'past_due' THEN 'past_due'
      ELSE 'inactive'
    END,
    -- Copy user subscription data to organization for backward compatibility
    stripe_customer_id = NEW.stripe_customer_id,
    subscription_id = NEW.stripe_subscription_id,
    current_period_end = NEW.current_period_end
  WHERE id IN (
    SELECT organization_id 
    FROM organization_members 
    WHERE user_id = NEW.user_id AND role = 'owner'
  );
  
  RETURN NEW;
END;
$function$
;

CREATE OR REPLACE FUNCTION public.transfer_organization_ownership(p_org_id uuid, p_new_owner_id uuid)
 RETURNS void
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
DECLARE
  v_requester uuid := auth.uid();
  v_is_requester_owner boolean;
  v_new_owner_membership_exists boolean;
BEGIN
  IF v_requester IS NULL THEN
    RAISE EXCEPTION 'Not authenticated';
  END IF;

  -- Ensure requester is current owner of the organization
  SELECT EXISTS (
    SELECT 1 FROM public.organization_members om
    WHERE om.organization_id = p_org_id
      AND om.user_id = v_requester
      AND om.role = 'owner'
  ) INTO v_is_requester_owner;

  IF NOT v_is_requester_owner THEN
    RAISE EXCEPTION 'Only the current owner can transfer ownership';
  END IF;

  -- Ensure the new owner is already a member of the organization
  SELECT EXISTS (
    SELECT 1 FROM public.organization_members om
    WHERE om.organization_id = p_org_id
      AND om.user_id = p_new_owner_id
  ) INTO v_new_owner_membership_exists;

  IF NOT v_new_owner_membership_exists THEN
    RAISE EXCEPTION 'New owner must be an existing member of the organization';
  END IF;

  -- Perform transfer atomically
  PERFORM pg_advisory_xact_lock(hashtext(p_org_id::text));

  -- Demote current owner (requester) to admin
  UPDATE public.organization_members
  SET role = 'admin'
  WHERE organization_id = p_org_id
    AND user_id = v_requester
    AND role = 'owner';

  IF NOT FOUND THEN
    RAISE EXCEPTION 'Ownership transfer failed: requester is not recorded as owner';
  END IF;

  -- Promote target member to owner
  UPDATE public.organization_members
  SET role = 'owner'
  WHERE organization_id = p_org_id
    AND user_id = p_new_owner_id;

  IF NOT FOUND THEN
    RAISE EXCEPTION 'Ownership transfer failed: target user is not a member';
  END IF;

  -- Ensure only one owner exists (optional hard guard)
  IF (SELECT COUNT(*) FROM public.organization_members
      WHERE organization_id = p_org_id AND role = 'owner') <> 1 THEN
    RAISE EXCEPTION 'Ownership transfer integrity check failed: unexpected owner count';
  END IF;
END;
$function$
;

