CREATE OR REPLACE FUNCTION public.user_has_role_in_org(user_email text, org_id uuid, required_role organization_role)
 RETURNS boolean
 LANGUAGE sql
 STABLE SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
  SELECT EXISTS (
    SELECT 1
    FROM organization_members om
    JOIN profiles p ON p.id = om.user_id
    WHERE p.email = user_email
      AND om.organization_id = org_id
      AND (
        om.role = required_role OR
        (required_role = 'member' AND om.role IN ('admin', 'owner')) OR
        (required_role = 'admin' AND om.role = 'owner')
      )
  );
$function$
;


